public class MathCalc {	public static void main(String[] args) {		double radius = 0.0;		double circleArea = 100.0;		int feet = 0;		int inches = 0;				double diameterEarth = 76000d;		double diameterSun = 865000d;				radius = Math.sqrt(circleArea / Math.PI);		feet = (int) Math.floor(radius);		inches = (int) Math.round(12.0 * (radius - feet));				System.out.println("The radius of a circle with area " + circleArea + " square feet is\n " + feet + " feet " + inches + " inches");				System.out.println("The volume of the earth is: " + calculateVolume(diameterEarth));		System.out.println("The volume of the sun is: " + calculateVolume(diameterSun));		System.out.println("The ratio of the volume of the sun to the earth is: " + calculateRatio(calculateVolume(diameterSun), calculateVolume(diameterEarth)));			}		/**	 * Calculate the volume of a circle.	 * @param diameter	 * @return	 */	private static double calculateVolume(double diameter) {		return (4 * Math.PI * (Math.pow((diameter/2), 3))) / 3;	}		private static double calculateRatio(double num1, double num2) {		return (num1/num2);	}}